frappe.ui.form.on('Patient Registration Identification', {
    full_age: function(frm) {
        // Get the age value from the field
        let age = frm.doc.full_age;

        // Check if the age is a valid number
        if (age && !isNaN(age)) {
            // Get the current date
            let today = new Date();

            // Calculate the birth year by subtracting the age from the current year
            let birth_year = today.getFullYear() - age;

            // Create a new date object for the approximate birth date
            let birth_date = new Date(birth_year, today.getMonth(), today.getDate());

            // Set the calculated birth date in the 'date_of_birth' field
            frm.set_value('date_of_birth', birth_date);
        } else {
            // Clear the date_of_birth field if the age is invalid
            frm.set_value('date_of_birth', null);
        }
    }
});

frappe.ui.form.on('Patient Registration Identification', {
    refresh: function(frm) {
        // Add a custom button
        frm.add_custom_button(__('Go to Patient'), function() {
            // Get the current doc.name (which is the unique identifier of the current document)
            let patient_registration_id = frm.doc.name;
            
            frappe.model.with_doctype('Patient', function() {
                    var patient_doc = frappe.model.get_new_doc('Patient');
                    patient_doc.custom_patient_mrno = patient_registration_id;

                    frappe.set_route('Form', 'Patient', patient_doc.name);
                });

   
        });
    }
});

frappe.ui.form.on('Patient', {
    refresh: function(frm) {
        // Add a custom button
        frm.add_custom_button(__('Receive Money'), function() {
            // Get the current doc.name (which is the unique identifier of the current document)
            let patient_registration_id = frm.doc.name;
            
            // Create a new Patient Payment Management document
            frappe.model.with_doctype('Patient Payment Management', function() {
                let patient_doc = frappe.model.get_new_doc('Patient Payment Management');
                // Set the patient field to the current patient's ID
                patient_doc.patient = patient_registration_id;

                // Insert the document and navigate to it
                frappe.db.insert(patient_doc).then(doc => {
                    frappe.set_route('Form', 'Patient Payment Management', doc.name);
                }).catch(err => {
                    frappe.msgprint(__('There was an issue creating the document: ') + err.message);
                });
            });
        });
    }
});

