function requestTriage(frm) {
    // Check if the form object and doc are valid
    if (frm && frm.doc) {
        // Create a new Vital Signs document
        frappe.model.with_doctype('Vital Signs', function() {
            let vital_sign_doc = frappe.model.get_new_doc('Vital Signs');

            // Set the fields from the Patient Encounter to the new Vital Signs document
            vital_sign_doc.patient = frm.doc.patient;
            vital_sign_doc.patient_name = frm.doc.patient_name;
            vital_sign_doc.custom_practionaer = frm.doc.practitioner;
            vital_sign_doc.custom_encounter_id = frm.doc.name;
            vital_sign_doc.custom_patient_status = 'Doctor\'s Request';

            // Insert the new document into the database without routing
            frappe.db.insert(vital_sign_doc).then(function(doc) {
                frappe.msgprint(__('Your Vital Signs Request Sent successfully.'));
            }).catch(function(error) {
                frappe.msgprint(__('Failed to create Vital Signs document.'));
                console.error(error);
            });
        });
    } else {
        // Display an error message if frm or frm.doc is not valid
        frappe.msgprint(__('Form object or document is invalid.'));
    }
}

frappe.ui.form.on('Patient Encounter', {
    encounter_date: function(frm) {
        // Calculate the due date as posting date + 30 days
        var postingDate = new Date(frm.doc.encounter_date);
        postingDate.setDate(postingDate.getDate() + 30);
        
        // Format the date to YYYY-MM-DD
        var dueDate = postingDate.toISOString().split('T')[0];

        // Set the due date in the form
        frm.set_value('custom_due_date', dueDate);
    }
});

