function wipe_custom_drug_status(frm) {
    // Iterate through each item in the drug prescription
    frm.doc.drug_prescription.forEach(function(item) {
        // Set the `custom_drug_status` field to an empty value
        item.custom_drug_status = '';
    });
    
    // Refresh the field to reflect changes in the UI
    frm.refresh_field('drug_prescription');
    
    // Optionally, save the document if you want to persist changes
    frm.save();
}

