frappe.ui.form.on('Patient Encounter', {
    refresh: function(frm) {
        // Check if the form is loaded with lab_test_prescription table
        if (frm.doc.lab_test_prescription && frm.fields_dict['lab_test_prescription']) {
            frm.doc.lab_test_prescription.forEach(function(row) {
                if (row.custom_results_status === "Results Ready") {
                    // Get the row element in the grid
                    let row_element = frm.fields_dict['lab_test_prescription'].grid.get_row(row.name).row;
                    
                    // Create a clickable span element
                    let clickable_span = $(`<span style="color:blue;cursor:pointer;">${row.custom_results_status}</span>`);

                    // Attach the click event to navigate to the lab test document
                    clickable_span.on('click', function() {
                        frappe.set_route('Form', 'Lab Test', row.custom_labtest_id);
                    });

                    // Replace the text in the grid with the clickable span
                    $(row_element).find(`[data-fieldname="custom_results_status"]`).html(clickable_span);
                }
            });
        }

        // Check if the form is loaded with custom_radiology_items table
        if (frm.doc.custom_radiology_items && frm.fields_dict['custom_radiology_items']) {
            frm.doc.custom_radiology_items.forEach(function(row) {
                if (row.results_status === "Results Ready") {
                    // Get the row element in the grid
                    let row_element = frm.fields_dict['custom_radiology_items'].grid.get_row(row.name).row;
                    
                    // Create a clickable span element
                    let clickable_span = $(`<span style="color:blue;cursor:pointer;">${row.results_status}</span>`);

                    // Attach the click event to navigate to the observation test document
                    clickable_span.on('click', function() {
                        frappe.set_route('Form', 'Observation', row.observe_id);
                    });

                    // Replace the text in the grid with the clickable span
                    $(row_element).find(`[data-fieldname="results_status"]`).html(clickable_span);
                }
            });
        }

        // Check if the form is loaded with procedure_prescription table
        if (frm.doc.procedure_prescription && frm.fields_dict['procedure_prescription']) {
            frm.doc.procedure_prescription.forEach(function(row) {
                if (row.custom_proceding_status === "Results Ready") {
                    // Get the row element in the grid
                    let row_element = frm.fields_dict['procedure_prescription'].grid.get_row(row.name).row;
                    
                    // Create a clickable span element
                    let clickable_span = $(`<span style="color:blue;cursor:pointer;">${row.custom_proceding_status}</span>`);

                    // Attach the click event to navigate to the Procedure test document
                    clickable_span.on('click', function() {
                        frappe.set_route('Form', 'Clinical Procedure', row.custom_procedure_id);
