frappe.ui.form.on('Patient', {
    custom_patient_mrno: function(frm) {
        if (frm.doc.custom_patient_mrno) {
            // Fetch the Patient Registration Identification record
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Patient Registration Identification',
                    filters: {
                        // Assuming 'mrno' is the field in Patient Registration Identification
                        // that corresponds to custom_patient_mrno
                        'name': frm.doc.custom_patient_mrno
                    }
                },
                callback: function(r) {
                    if (r.message) {
                        // Set the date_of_birth from the fetched record
                        frm.set_value('dob', r.message.date_of_birth);
                        
                        // Fetch the patient_name
                        frappe.db.get_value('Patient Registration Identification', frm.doc.custom_patient_mrno, 'patient_name')
                        .then(r => {
                            if (r.message && r.message.patient_name) {
                                let patient_name = r.message.patient_name;
                                let name_parts = patient_name.split(' ');
                                console.log(patient_name,name_parts)
                                // frm.set_value('patient_name', patient_name);
                                // Splitting the name into first, middle, and last names
                                if (name_parts.length === 2) {
                                    frm.set_value('first_name', name_parts[0]);
                                    frm.set_value('last_name', name_parts[1]);
                                    frm.set_value('middle_name', '');
                                } else if (name_parts.length === 3) {
                                    frm.set_value('first_name', name_parts[0]);
                                    frm.set_value('middle_name', name_parts[1]);
                                    frm.set_value('last_name', name_parts[2]);
                                } else if (name_parts.length > 3) {
                                    frm.set_value('first_name', name_parts[0]);
                                    frm.set_value('last_name', name_parts[name_parts.length - 1]);
                                    frm.set_value('middle_name', name_parts.slice(1, -1).join(' '));
                                } else {
                                    frm.set_value('first_name', patient_name);
                                    frm.set_value('middle_name', '');
                                    frm.set_value('last_name', '');
                                }
                            }
                        });

                    } else {
                        // If no matching record is found, clear the date_of_birth
                        frm.set_value('dob', null);
                        frappe.msgprint(__('No matching Patient Registration Identification found.'));
                    }
                }
            });
        } else {
            // If custom_patient_mrno is cleared, clear the date_of_birth and name fields
            frm.set_value('dob', null);
            frm.set_value('first_name', null);
            frm.set_value('middle_name', null);
            frm.set_value('last_name', null);
        }
    }
});

