frappe.ui.form.on('Patient', {
    custom_self_request: function(frm) {
        // Define the dialog
        let d = new frappe.ui.Dialog({
            title: 'Create an Invoice to Receive Payments',
            fields: [
                {
                    fieldname: 'items',
                    label: 'Items',
                    fieldtype: 'Table',
                    fields: [
                        {
                            fieldname: 'item',
                            label: 'Item',
                            fieldtype: 'Link',
                            options: 'Item',
                            reqd: 1,
                            in_list_view: 1,
                            get_query: function() {
                                return {
                                    filters: [
                                        ['item_group', 'in', getItemGroups()]
                                    ]
                                };
                            },
                            change: function() {
                                handleItemChange(this.grid_row, frm.doc.default_price_list);
                            }
                        },
                        {
                            fieldname: 'qty',
                            label: 'Quantity',
                            fieldtype: 'Float',
                            reqd: 1,
                            in_list_view: 1,
                            change: function() {
                                updateAmount(this.grid_row);
                            }
                        },
                        {
                            fieldname: 'rate',
                            label: 'Rate',
                            fieldtype: 'Currency',
                            reqd: 1,
                            in_list_view: 1,
                            change: function() {
                                updateAmount(this.grid_row);
                            }
                        },
                        {
                            fieldname: 'amount',
                            label: 'Amount',
                            fieldtype: 'Currency',
                            reqd: 1,
                            in_list_view: 1
                        }
                    ],
                    in_place_edit: true,
                    data: [],
                    get_data: function() {
                        return [];
                    }
                },
                {
                    fieldname: 'grand_total',
                    label: 'Grand Total',
                    fieldtype: 'Currency',
                    read_only: 1
                }
            ],
            primary_action_label: 'Invoice Now',
            primary_action: function(data) {
                // Separate drug items and non-drug items
                const drugItems = [];
                const nonDrugItems = [];

                data.items.forEach(item => {
                    if (isDrugItem(item.item)) {
                        drugItems.push(item);
                    } else {
                        nonDrugItems.push(item);
                    }
                });

                // Create documents based on the items
                if (drugItems.length > 0) {
                    createPharmacyDoc(frm, drugItems);
                }
                if (nonDrugItems.length > 0) {
                    createSalesInvoice(frm, nonDrugItems, d.get_value('grand_total'));
                }

                d.hide(); // Close the dialog after processing
            }
        });

        function getItemGroups() {
            return [
                'Drugs',
                'Services',
                'Tablets',
                'Consumables',
                'Minor Procedure',
                'Major Procedure',
                'Laboratory services-Outsourced',
                'Laboratory services-In-house',
                'Laboratory',
                'X-ray scanning services',
                'Ultrasound Scan Services',
                'Radiology Services',
                'Procedures'
            ];
        }

        function handleItemChange(grid_row, price_list) {
            let selected_item = grid_row.doc.item;
            if (selected_item) {
                frappe.call({
                    method: 'frappe.client.get_value',
                    args: {
                        doctype: 'Item Price',
                        filters: {
                            item_code: selected_item,
                            price_list: price_list
                        },
                        fieldname: 'price_list_rate'
                    },
                    callback: function(r) {
                        if (r.message && r.message.price_list_rate) {
                            grid_row.doc.rate = r.message.price_list_rate;
                            updateAmount(grid_row);
                        } else {
                            frappe.msgprint(__('Price not found for the selected item.'));
                        }
                    }
                });
            }
        }

        function updateAmount(grid_row) {
            grid_row.doc.amount = grid_row.doc.rate * grid_row.doc.qty;
            grid_row.refresh();
            updateGrandTotal();
        }

        function updateGrandTotal() {
            let items = d.get_value('items');
            let grand_total = items.reduce((sum, item) => sum + item.amount, 0);
            d.set_value('grand_total', grand_total);
        }

        function isDrugItem(itemCode) {
            let isDrug = false;

            // Check if the item belongs to the Drug item group
            frappe.call({
                method: 'frappe.client.get_value',
                args: {
                    doctype: 'Item',
                    filters: { name: itemCode },
                    fieldname: 'item_group'
                },
                async: false, // Keep it synchronous for this check
                callback: function(r) {
                    if (r.message && r.message.item_group === 'Drugs') {
                        isDrug = true;
                    }
                }
            });

            return isDrug;
        }

        function createSalesInvoice(frm, items, grand_total) {
            const patient = frm.doc.name;
            const customer = frm.doc.customer;
            const price_list = frm.doc.default_price_list;
            const cost_center = frm.doc.custom_consulting_department;
            const patient_id = frm.doc.name;
            const company = frappe.defaults.get_global_default("company");
            const posting_date = frappe.datetime.get_today();
            const due_date = frappe.datetime.add_days(posting_date, 15);

            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Sales Invoice',
                    filters: { custom_payment_id: patient_id },
                    fields: ['name']
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        frappe.msgprint(__('An invoice already exists for this patient.'));
                    } else {
                        frappe.call({
                            method: 'frappe.client.insert',
                            args: {
                                doc: {
                                    doctype: 'Sales Invoice',
                                    patient: patient,
                                    customer: customer,
                                    cost_center: cost_center,
                                    selling_price_list: price_list,
                                    company: company,
                                    set_posting_time: 1,
                                    posting_date: posting_date,
                                    custom_payment_id: patient_id,
                                    due_date: due_date,
                                    items: items.map(row => ({
                                        item_code: row.item,
                                        qty: row.qty,
                                        rate: row.rate,
                                        custom_self_request: 1,
                                        amount: row.amount,
                                        cost_center: cost_center
                                    })),
                                    grand_total: grand_total
                                }
                            },
                            callback: function(r) {
                                if (r.message) {
                                    refreshAndSubmitInvoice(r.message.name);
                                    frappe.msgprint(__('Sales Invoice created successfully'));
                                } else {
                                    frappe.msgprint(__('Failed to create Sales Invoice'));
                                }
                            },
                            error: function(err) {
                                frappe.msgprint(__('There was an error while creating the Sales Invoice.'));
                            }
                        });
                    }
                }
            });
        }

        function createPharmacyDoc(frm, items) {
            const patient = frm.doc.name;
            const price_list = frm.doc.default_price_list;
            const cost_center = frm.doc.custom_consulting_department;
            const patient_id = frm.doc.name;
            const custom_patient_age = frm.doc.custom_patient_age;
            const custom_consulting_doctor = frm.doc.custom_consulting_doctor;
            const company = frappe.defaults.get_global_default("company");
            const posting_date = frappe.datetime.get_today();

            frappe.call({
                method: 'frappe.client.insert',
                args: {
                    doc: {
                        doctype: 'Pharmacy',
                        patient: patient,
                        patient_age: custom_patient_age,
                        practitioner: custom_consulting_doctor,
                        custom_cost_center: cost_center,
                        price_list: price_list,
                        company: company,
                        encounter_date: posting_date,
                        drug_prescription: items.map(row => ({
                            drug_code: row.item,
                            qty: row.qty,
                            rate: row.rate,
                            self_request: 1,
                            amount: row.amount,
                            dosage_form: "Cream",
                            dosage: "1-0-1",
                            period: "1 Day",
                        }))
                    }
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__('Pharmacy document created successfully'));
                    } else {
                        frappe.msgprint(__('Failed to create Pharmacy document'));
                    }
                },
                error: function(err) {
                    frappe.msgprint(__('There was an error while creating the Pharmacy document.'));
                }
            });
        }

        function refreshAndSubmitInvoice(invoice_name) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Sales Invoice',
                    name: invoice_name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.call({
                            method: 'frappe.client.submit',
                            args: {
                                doc: r.message
                            },
                            callback: function(r) {
                                if (r.message) {
                                    frappe.msgprint(__('Sales Invoice {0} has been submitted successfully.', [invoice_name]));
                                } else {
                                    frappe.msgprint(__('Failed to submit Sales Invoice {0}.', [invoice_name]));
                                }
                            },
                            error: function(err) {
                                frappe.msgprint(__('There was an error while submitting the Sales Invoice.'));
                            }
                        });
                    } else {
                        frappe.msgprint(__('Failed to refresh Sales Invoice {0}.', [invoice_name]));
                    }
                },
                error: function(err) {
                    frappe.msgprint(__('There was an error while refreshing the Sales Invoice.'));
                }
            });
        }

        // Show the dialog
        d.show();
    }
});

