function populateTableEmergency(frm) {
    // Clear the existing rows in the child table first
    const existingRows = frm.doc.patient_identification || [];
    if (existingRows.length > 0) {
        frm.clear_table('patient_identification');
        frm.refresh_field('patient_identification');
    }

    // Get the filters
    const emergency_id = frm.doc.emergency_id;

    if (emergency_id) {
        // Call the server-side method
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Emergency',
                filters: {
                    name: emergency_id // Assuming emergency_id is the identifier
                },
                fields: ['nok_name', 'relationship', 'phone','referal'] // Fetch the necessary fields
            },
            callback: function(response) {
                console.log(response)
                const emergency = response.message || [];
                console.log('Fetched emergency data:', emergency);

                if (emergency.length > 0) {
                    frm.set_value('refered_from', emergency[0].referal);
                    // Add rows to the child table
                    emergency.forEach(item => {
                        let child = frm.add_child('patient_identification');
                        frappe.model.set_value(child.doctype, child.name, 'naxtof_name', item.nok_name);
                        frappe.model.set_value(child.doctype, child.name, 'relationship', item.relationship);
                        frappe.model.set_value(child.doctype, child.name, 'phone_no', item.phone);
                    });

                    // Refresh the child table
                    frm.refresh_field('patient_identification');
                } else {
                    frappe.msgprint(__('No emergency contact found with the given ID.'));
                }
            },
            error: function(error) {
                frappe.msgprint(__('An error occurred while fetching emergency contact data.'));
                console.error('Error fetching emergency data:', error);
            }
        });
    } else {
        frappe.msgprint(__('Please select an emergency ID.'));
    }
}

