function get_statement_details(frm) {
    frappe.call({
        method: 'hmh_custom_app.custom_api.statement.statement.get_sales_invoice_details_and_payments',
        args: {
            customer: frm.doc.customer,
            from_date: frm.doc.from_date,
            to_date: frm.doc.to_date,
            cost_center: frm.doc.cost_center
        },
        callback: function(r) {
            console.log(r);
            if (r.message) {
                let data = r.message.sales_invoice_data;
                let running_balance = 0;  // Variable to keep track of running balance

                // Clear existing child table data
                frm.clear_table('statement_details');

                // Populate the child table with the fetched invoice data 
                data.forEach(function(invoice) {
                    let child = frm.add_child('statement_details');
                    frappe.model.set_value(child.doctype, child.name, 'item_code', invoice.item_code);
                    frappe.model.set_value(child.doctype, child.name, 'qty', invoice.qty);
                    frappe.model.set_value(child.doctype, child.name, 'quantity', invoice.qty);
                    frappe.model.set_value(child.doctype, child.name, 'rate', invoice.rate);
                    frappe.model.set_value(child.doctype, child.name, 'amount', invoice.amount);
                    frappe.model.set_value(child.doctype, child.name, 'invoice_date', invoice.posting_date); // Set invoice date
                    frappe.model.set_value(child.doctype, child.name, 'invoice_vourcher', invoice.invoice_name); 
           
                    // Update the running balance
                    running_balance += invoice.amount;  // Add invoice amount to running balance
                    frappe.model.set_value(child.doctype, child.name, 'running_balance', running_balance); // Set running balance
                });

                // Populate payment details in the child table
                r.message.payments.forEach(function(payment) {
                    let child = frm.add_child('statement_details');
                    frappe.model.set_value(child.doctype, child.name, 'payment_entry', payment.payment_entry_name);
                    frappe.model.set_value(child.doctype, child.name, 'station_pe', payment.cost_center);
                    frappe.model.set_value(child.doctype, child.name, 'invoice_date', payment.posting_date); // Set payment date
                    frappe.model.set_value(child.doctype, child.name, 'paid_amount', payment.paid_amount); // Set paid amount
                    
                    // Update the running balance with paid amount
                    running_balance -= payment.paid_amount; // Subtract paid amount from running balance
                    frappe.model.set_value(child.doctype, child.name, 'running_balance', running_balance); // Set updated running balance
                });

                // Populate GL Entry details in the child table
                r.message.gl_entries.forEach(function(gl_entry) {
                    let child = frm.add_child('statement_details');
                    frappe.model.set_value(child.doctype, child.name, 'journal_date', gl_entry.posting_date); // Set journal date
                    frappe.model.set_value(child.doctype, child.name, 'station_jl', gl_entry.cost_center); // Set cost center
                    frappe.model.set_value(child.doctype, child.name, 'voucher_no', gl_entry.voucher_no); // Set voucher number
                    frappe.model.set_value(child.doctype, child.name, 'amount', gl_entry.debit); // Set debit as amount
                    frappe.model.set_value(child.doctype, child.name, 'paid_amount', gl_entry.credit); // Set credit as paid amount
                    
                    // Update the running balance with GL Entry amounts
                    running_balance += gl_entry.debit;  // Add debit to running balance
                    running_balance -= gl_entry.credit;  // Subtract credit from running balance
                    frappe.model.set_value(child.doctype, child.name, 'running_balance', running_balance); // Set updated running balance
                });

                // Refresh the field to display the updated child table
                frm.refresh_field('statement_details');

                // Recalculate totals after populating the child table
                calculateTotals(frm);
            }
        }
    });
}

function calculateTotals(frm) {
    var total_amount = 0;
    var total_paid = 0;
    frm.doc.statement_details.forEach(function(item) {
        total_amount += item.amount || 0;  // Add amount, default to 0 if undefined
        total_paid += item.paid_amount || 0;  // Add paid amount, default to 0 if undefined
    });
    var outstanding_amount = total_amount - total_paid;
    frm.set_value('total_invoices', total_amount);  // Set total invoices
    frm.set_value('total_paid', total_paid);  // Set total paid
    frm.set_value('total_outstanding_amount', outstanding_amount);  // Set total outstanding amount

    // Refresh the parent fields
    frm.refresh_field('total_invoices');
    frm.refresh_field('total_paid');
    frm.refresh_field('total_outstanding_amount');
}

