function show_emergency_dialog(frm) {
    let d = new frappe.ui.Dialog({
        title: 'Enter Emergency Details',
        fields: [
            {
                label: 'NOK Name',
                fieldname: 'nok_name',
                fieldtype: 'Data',
                reqd: 1
            },
            {
                label: 'Country Code',
                fieldname: 'country_code',
                fieldtype: 'Select',
                options: [
                    '+1 (USA/Canada)', '+44 (UK)', '+61 (Australia)', '+91 (India)', '+256',
                    '+254 (Kenya)', '+27 (South Africa)', '+49 (Germany)', '+81 (Japan)', '+86 (China)'
                ],
                'default': '+256-',
                reqd: 1,
                read_only: 1
            },
            {
                label: 'NOK Phone',
                fieldname: 'phone',
                fieldtype: 'Data',
                'default': '7',  // Fixed '7' at the beginning
                reqd: 1
            },
            {
                label: 'Relationship',
                fieldname: 'relationship',
                fieldtype: 'Data',
                reqd:1
            },
            {
                label: 'Referred From',
                fieldname: 'referal',
                fieldtype: 'Data'
            }
        ],
        primary_action_label: 'Submit',
        primary_action: function(data) {
            // Validate and correct the phone number
            let phone_number = data.phone;

            // Remove any leading zero if present, except for the fixed '7'
            if (phone_number.startsWith('07')) {
                phone_number = phone_number.replace(/^07/, '7');
            }

            if (validate_phone_number(phone_number)) {
                // Concatenate country code with the phone number
                let full_phone = data.country_code.replace(/\s/g, '') + phone_number;

                // Create Emergency doc with the concatenated phone number
                create_emergency_doc(frm, {
                    nok_name: data.nok_name,
                    phone: full_phone,
                    relationship: data.relationship,
                    referal: data.referal
                });
                d.hide();
            } else {
                frappe.msgprint(__('Please enter a valid phone number.'));
            }
        }
    });

    d.show();
}

// Validate that the phone number contains only digits and has a valid length
function validate_phone_number(phone) {
    const phone_regex = /^[0-9]{9,12}$/;  // Accepts phone numbers with 9 to 12 digits
    return phone_regex.test(phone);
}

