}

// Helper function to extract the template part from the test key
function extractTemplateFromTestKey(test_key) {
    // Split the key by '-' to separate the components
    let parts = test_key.split('-');

    // Check if the number of parts is sufficient (should be at least 4 to get a valid template)
    if (parts.length >= 4) {
        // Get the template by joining parts from the 3rd index onward (to include all template parts)
        return parts.slice(3).join('-').trim(); // This combines the remaining parts into the template
    } else {
        return ''; // Return empty if the structure is not as expected
    }
}

