// Function to fetch and process lab tests
function fetch_lab_tests(frm) {
    frappe.call({
        method: "hmh_custom_app.custom_api.reports.latest_test.fetch_patient_labtest",  // Backend method to fetch lab tests
        args: {
            patient: frm.doc.patient  // Ensure the 'patient' field is present in the form
        },
        callback: function(response) {
            console.log(response);
            if (response.message) {
                let data = response.message;

                // Process kin details
                if (data.kin_details) {
                    frm.set_value('custom_nok_name', data.kin_details.naxtof_name || '');
                    frm.set_value('custom_nok_relationship', data.kin_details.relationship || '');
                    frm.set_value('custom_nok_phone_no', data.kin_details.phone_no || '');
                }

                // Process patient details
                if (data.patient_details) {
                    frm.set_value('custom_address', data.patient_details.village || '');
                }

                // Clear existing child table (report_details)
                frm.clear_table('custom_report_details');

                // Loop through the grouped lab test results by date
                Object.keys(data.lab_tests_grouped).forEach(function(date) {
                    let date_group = data.lab_tests_grouped[date];

                    // Iterate over the test keys within each date group
                    Object.keys(date_group).forEach(function(test_key) {
                        let test_data = date_group[test_key];

                        // Extract the template from the test key
                        let template = extractTemplateFromTestKey(test_key); // Extract the template correctly

                        // Process normal tests
                        if (Array.isArray(test_data.normal_tests)) {
                            test_data.normal_tests.forEach(function(test) {
                                add_child_row(frm, {
                                    test_name: test.lab_test_name,
                                    result_value: test.result_value,
                                    lab_technician_name: test.employee_name,
                                    doctor_name: test.practitioner_name,
                                    date: date,
                                    template: template // Set template from test key
                                });
                            });
                        }

                        // Process descriptive tests
                        if (Array.isArray(test_data.descriptive_tests)) {
                            test_data.descriptive_tests.forEach(function(test) {
                                add_child_row(frm, {
                                    test_name: test.lab_test_particulars,
                                    result_value: test.result_value,
                                    lab_technician_name: test.employee_name,
                                    doctor_name: test.practitioner_name,
                                    date: date,
                                    template: template // Set template from test key
                                });
                            });
                        }

                        // Process organism tests
                        if (Array.isArray(test_data.organism_tests)) {
                            test_data.organism_tests.forEach(function(test) {
                                add_child_row(frm, {
                                    test_name: test.organism,
                                    result_value: test.colony_population,
                                    lab_technician_name: test.employee_name,
                                    doctor_name: test.practitioner_name,
                                    date: date,
                                    template: template // Set template from test key
                                });
                            });
                        }
                    });
                });

                // Refresh the child table after adding rows
                frm.refresh_field('custom_report_details');
            } else {
                frappe.msgprint(__('No lab tests found for the selected patient.'));
            }
        },
        error: function(err) {
            frappe.msgprint(__('An error occurred while fetching lab tests.'));
            console.error(err);
        }
    });
}

// Helper function to add rows to the child table
function add_child_row(frm, row_data) {
    let child_row = frm.add_child('custom_report_details');
    Object.keys(row_data).forEach(function(key) {
        child_row[key] = row_data[key];
    });
}

