                            primary_action_label: __('Submit'),
                            primary_action(values) {
                                // Set the custom_reason field with the entered value
                                frm.set_value('custom_reason', values.custom_reason);
                                frm.refresh_field('custom_reason');
                                dialog.hide(); // Close the dialog

                                // Proceed to create the Sales Invoice
                                create_sales_invoice(frm, values.custom_reason);
                            }
                        });

                        dialog.show(); // Show the dialog
                    }
                });
            });
        }
    }
});

// Function to create a Sales Invoice
function create_sales_invoice(frm, reason) {
    const patient = frm.doc.name;
    const customer = frm.doc.customer;
    const time = frm.doc.custom_time;

    frappe.call({
        method: 'hmh_custom_app.custom_api.vitual_signs.create_sales_invoice',
        args: {
            patient: patient,
            customer: customer,
            reason: reason
        },
        callback: function(r) {
            if (r.message) {
                frappe.msgprint(__('Sales Invoice created successfully.'));

                // Proceed to create the Patient Encounter
                create_patient_encounter(frm);
            } else {
                frappe.msgprint(__('Failed to create Sales Invoice.'));
            }
        },
        error: function(err) {
            frappe.msgprint(__('There was an error while creating the Sales Invoice.'));
        }
    });
}

// Function to create a Patient Encounter
function create_patient_encounter(frm) {
    frappe.call({
        method: 'hmh_custom_app.custom_api.vitual_signs.create_patient_encounter',
        args: {
            doc_name: frm.doc.name
        },
        callback: function(r) {
            if (r.message) {
                frappe.msgprint(__('Patient Encounter created successfully.'));
            } else {
                frappe.msgprint(__('Failed to create Patient Encounter.'));
            }
        },
        error: function(err) {
            frappe.msgprint(__('There was an error while creating the Patient Encounter.'));
        }
    });
}

