frappe.ui.form.on('Post Anaesthesia Care Unit', {
    // Trigger calculation when any of the fields in 'modified_aldrete_score' child table change
    modified_aldretes_score: {
        // Trigger on changes to any field in the child table
        on_change: function(frm, cdt, cdn) {
            let row = locals[cdt][cdn];
            let total_score = 0;

            // Calculate total score based on the selected field values

            // Breathing
            if (row.breathing == 'Able to breathe & cough') {
                total_score += 2;
            } else if (row.breathing == 'Shallow breaths, weak cough') {
                total_score += 1;
            } else if (row.breathing == 'Very shallow breathing') {
                total_score += 0;
            }

            // Consciousness
            if (row.consciousness == 'Fully conscious') {
                total_score += 2;
            } else if (row.consciousness == 'Easily arousable') {
                total_score += 1;
            } else if (row.consciousness == 'Deeply sedated') {
                total_score += 0;
            }

            // SpO2
            if (row.spo2 == '> 97% or = 97% room air') {
                total_score += 2;
            } else if (row.spo2 == '> 97% with O2') {
                total_score += 1;
            } else if (row.spo2 == '< 96% with O2') {
                total_score += 0;
            }

            // BP
            if (row.bp == 'Normal preop value') {
                total_score += 2;
            } else if (row.bp == 'Within 20% of normal') {
                total_score += 1;
            } else if (row.bp == '> 20% of normal') {
                total_score += 0;
            }

            // Motor
            if (row.motor == 'Moves extremities easily') {
                total_score += 2;
            } else if (row.motor == 'Moves only when aroused') {
                total_score += 1;
            } else if (row.motor == 'Little or no movement') {
                total_score += 0;
            }

            // Pain
            if (row.pain >= 0 && row.pain <= 3) {
                total_score += 2;
            } else if (row.pain >= 4 && row.pain <= 6) {
                total_score += 1;
            } else if (row.pain >= 7 && row.pain <= 10) {
                total_score += 0;
            }

            // Set the total_score for the row
            row.total_score = total_score;

            // Save the total_score in the database
            frappe.model.set_value(row.doctype, row.name, 'total_score', total_score);

            // Refresh the grid to reflect the changes
            frm.refresh_field('modified_aldrete_score');
        }
    }
});

